import React, { Component } from "react";
import Form from "./Form"
import axios from "axios";


class Control extends Component {
    constructor(props) {
        super(props);
        this.state = {
            text0: props.texts.topText,
            text1: props.texts.bottomRText
        };
        this.generateMeme = props.generateMeme.bind(this);
    }


    fetchMemes = async (event) => {
        event.preventDefault();
        const memeAPI = "https://api.imgflip.com/caption_image";
        try {
            const response = await axios.get(memeAPI, {
                params: {
                    text0: this.state.text0,
                    text1: this.state.text1
                }
            });
            const data = response.data;
            console.log(data);
            this.generateMeme(data.memes);
        } catch (err) {
            console.log(err);
        }
    }

    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    render() {
        return (
            <section className="section">
                <div className="field has-addons">
                    <div className="control is-expanded">
                    <input
                        className="input is-large is-fullwidth is-family-monospace"
                        id="top-input"
                        placeholder="Top text"
                        type="text"
                        name="topText"
                        value={this.state.topText}
                        onChange={this.handleChange}
                    />
                    </div>
                </div>
                <div className="field has-addons">
                    <div className="control is-expanded">
                    <input
                        className="input is-large is-fullwidth is-family-monospace"
                        id="bottom-input"
                        placeholder="Bottom text"
                        type="text"
                        name="bottomText"
                        value={this.state.bottomText}
                        onChange={this.handleChange}
                    />
                    </div>
                </div>
                <div className="field has-addons">
                    <div className="control is-expanded">
                        <button 
                            className="button is-link is-large is-fullwidth" 
                            id="go-btn"
                            onClick={this.fetchMemes}>
                            Go!
                        </button>
                    </div>
                </div>
            </section>
        );
    }

}

export default Control;
