import React, { Component } from "react";
import Header from "./components/Header"
import Control from "./components/Control"
import Output from "./components/Output"
import Footer from "./components/Footer"


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      imageSrc: "data:image/svg+xml,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20viewBox%3D%220%200%20512%20512%22%3E%3Cpath%20d%3D%22M224%20387.814V512L32%20320l192-192v126.912C447.375%20260.152%20437.794%20103.016%20380.93%200%20521.287%20151.707%20491.48%20394.785%20224%20387.814z%22%2F%3E%3C%2Fsvg%3E",

      texts: {
        topText: "",
        bottomText: ""
      }

    }
  }

  generateMeme = (images) => {
    const image = images[Math.floor(Math.random() * images.length)]; 
    this.setState({
      imageSrc: image.url
    });
  }

  //prevent default for image and text
  // use async
  updateText = (texts) => {
    this.setState({
      texts: {...texts}
    });
  }

  render() {
    return (
      <>
        <Header />
        <Control generateMeme={this.generateMeme} texts={this.state.texts} updateText={this.updateText}/>
        <Output imageSrc={this.state.imageSrc} texts={this.state.texts} /> 
        <Footer />
      </>
    );
  }


}

export default App;
