import react, { Component } from "react";
import Form from "./Form"
import axios from "axios";


class Control extends Component {
    constructor(props) {

    }


    fetchMemes = async (event) => {
        event.preventDefault();
        const memeAPI = "https://api.imgflip.com/caption_image";
        try {
            const response = await axios.get(memeAPI, {
                params: {
                    text0: this.props.texts.top,
                    text1: this.props.texts.bottom
                }
            });
            const data = response.data;
            this.props.generateMeme(data.memes);
        } catch (err) {
            console.log(err);
        }
    }



    render() {
        return (
            <section class="section">
                <Form />
                <div className="field has-addons">
                    <div className="control is-expanded">
                        <button 
                            class="button is-link is-large is-fullwidth" 
                            id="go-btn"
                            onClick={this.fetchMemes}>
                            Go!
                        </button>
                    </div>
                </div>
            </section>


        );
    }



}

export default Control;
